    public static int lengthOfLongestSubstring_leetcode_user(String s) {
        // 储存所有字符上一次出现的位置
        int[] last = new int[128];
        for(int i = 0; i < 128; i++) {
            last[i] = -1;
        }
        int n = s.length();

        int res = 0;
        int start = 0; // 窗口开始位置。窗口中不存在重复内容，因此如果有重复内容，则让窗口滑动一次，同时记录当前重复值之间的距离
        for(int i = 0; i < n; i++) {
            int index = s.charAt(i);
            start = Math.max(start, last[index] + 1);
            res   = Math.max(res, i - start + 1);
            last[index] = i;  // 记录字符index本次出现的位置i
        }

        return res;
    }
